import React from 'react';
import './ImageGallery.css';

const ImageGallery = () => {
  const images = [
    {
      src: "https://images.unsplash.com/photo-1586771107445-d3ca888129ff?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
      alt: "Women farmers in refugee settlement",
      caption: "Women's agricultural cooperative in Nyumanzi"
    },
    {
      src: "https://images.unsplash.com/photo-1595113316349-9fa4eb24f884?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
      alt: "Vegetable farming in refugee camp",
      caption: "Sustainable vegetable cultivation"
    },
    {
      src: "https://images.unsplash.com/photo-1574323347407-f5e1ad6d020b?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
      alt: "Community farming training",
      caption: "Agricultural training session"
    },
    {
      src: "https://images.unsplash.com/photo-1559827260-dc66d52bef19?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
      alt: "Crop harvesting in Uganda",
      caption: "Harvest season in refugee settlement"
    },
    {
      src: "https://images.unsplash.com/photo-1592982736920-1ad1f9d2c8b4?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
      alt: "Youth farming program",
      caption: "Youth agricultural empowerment"
    },
    {
      src: "https://images.unsplash.com/photo-1500382017468-9049fed747ef?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
      alt: "Maize cultivation",
      caption: "Maize farming with improved seeds"
    }
  ];

  return (
    <section className="image-gallery">
      <div className="container">
        <h2>Our Work in Action</h2>
        <p className="gallery-subtitle">Witness the transformation happening in Nyumanzi and Ayilo refugee settlements</p>
        <div className="gallery-grid">
          {images.map((image, index) => (
            <div key={index} className="gallery-item">
              <img src={image.src} alt={image.alt} />
              <div className="gallery-overlay">
                <p>{image.caption}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ImageGallery;
