import React, { useState, useEffect } from 'react';
import { Menu, X } from 'lucide-react';
import './Navbar.css';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 50);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="nav-container">
        <div className="nav-logo">
          <h2>PuurKu Initiative</h2>
        </div>

        <ul className={`nav-menu ${isOpen ? 'active' : ''}`}>
          <li><a href="#about" onClick={() => setIsOpen(false)}>About</a></li>
          <li><a href="#agriculture" onClick={() => setIsOpen(false)}>Agriculture</a></li>
          <li><a href="#impact" onClick={() => setIsOpen(false)}>Impact</a></li>
          <li><a href="#resources" onClick={() => setIsOpen(false)}>Resources</a></li>
          <li><a href="#contact" className="cta-btn" onClick={() => setIsOpen(false)}>Support Us</a></li>
        </ul>

        <button className="mobile-menu" onClick={() => setIsOpen(!isOpen)}>
          {isOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
